INSERT INTO `news_videos` (`id`, `title`, `video`, `duration`, `content`, `created_at`, `updated_at`, `deleted_at`, `news_datetime`, `title_en`, `content_en`, `web_identity`, `tag`) VALUES
(1, 'Pemerintah Segera Resmikan Komite Nasional Keuangan Syariah', 'coZ0oYkXtx0', '00:03:58', '', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(2, 'KNKS Diharapkan Pacu Keuangan Syariah Nasional', 'khcslqB4zZE', '00:02:17', '(Antara)-Presiden Joko Widodo meresmikan pembentukan Komite Nasional Keuangan Nasional, KNKS. Kelahiran komite ini diharapkan mampu mendorong Indonesia menjadi pusat ekonomi dan keuangan syariah tersebesar di dunia. copyright © AntaraTV LKBN Antara - Indonesia', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(3, 'BANK INDONESIA SIAPKAN ROADMAP EKONOMI SYARIAH', 'rADjEQkBlXo', '00:01:44', 'IDXCHANNEL - Dalam meningkatkan perekonomian syariah Bank Indonesia tengah menyiapkan roadmap ekonomi syariah yang akan segera diluncurkan dimana potensi ekonomi syariah begitu besar. Ekonomi syariah kedepan diharapkan akan mendorong akselerasi ekonomi syariah dimasyarakat, melihat potensi ini Bank Indonesia terus menginisiasi pengembangan ekonomi dan keuangan syariah dengan bekerjasama dengan pemerintah.   Melalui Komite Nasional Keuangan Syariah (KNKS) Bank Indonesia tengah menyusun roadmap untuk meningkatkan potensi ekonomi keuangan syariah, dalam prosesnya Bank Indonesia mendorong Bussines Matching antara pelaku ekonomi syariah berbasis pesantren dengan pembeli potensial dari negara lainya. Diharapkan cara ini akan lebih mendorong permintaan yang ditujukan kepada produk syariah dalam negeri.   Direktur Departemen Ekonomi dan Keuangan Syariah Bank Indonesia Anwar Bashori menegaskan bahwa Bank Indonesia telah meniapkan tiga pilar tetap yaitu penguatan ekonomi syariah, penguatan sektor keuangan syariah untuk pembiayaan dan penguatan riset assessment dan edukasi.', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(4, 'Rapat Pleno Komite Nasional Keuangan Syariah', 'CcrA0_8UkX0', '00:05:41', '', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(5, 'Peluncuran Komite Nasional Keuangan Syariah dan Pembukaan SILAKNAS IAEI', 'm7QQctvbdEc', '00:06:10', '', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(10, 'Indonesia Tuan Rumah Keuangan Syariah Dunia 2024', '2ZPix5AwDyc', '00:03:02', '<p>Jum&#39;at 30 Agustus 2019 - Hadir dalam event Muslim Lifefest 2019 di Jakarta Convention Centre Komite Nasional Keuangan Syariah atau KNKS turut mengedukasi umat islam tentang potensi dan peluang perbankan syariah. Proses edukasi disampaikan dalam seminar bertajuk &quot;Perbankan Syariah: Peluang Dan Tantangan Mendukung Visi Indonesia Menjadi Tuan Rumah Ekonomi Keuangan Syariah Dunia Tahun 2024&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>KKNS hadir sebagai lembaga yang berperan untuk menyamakan persepsi dan mewujudkan sinergi antara para regulator, pemerintah, dan industri keuangan syariah untuk menciptakan sistem keuangan syariah yang selaras dan progresif untuk pertumbuhan ekonomi Indonesia.</p>', '2019-09-04 01:40:52', '2019-09-04 01:40:52', NULL, '2019-09-04 08:40:55', NULL, NULL, 'kneks', NULL),
(7, 'BAZNAS News - Sinergi KNKS dengan BAZNAS menuju Indonesia sebagai Pusat Ekonomi Syariah Dunia', 'bv3uErg_dNs', '00:02:11', 'Pertemuan antara KNKS dan BAZNAS pada 31 Januari, membahas bagaimana pentingnya peran zakat yang signifikan dalam membangun visi Indonesia menjadi pusat ekonomi syariah dunia. Kerjasama yang dilakukan antara lain penelitian, pengembangan regulasi dan pengembangan sistem informasi. Apa yang akan dikerjakan oleh KNKS justru telah banyak dilakukan BAZNAS sehingga kerjasama ini berkomitmen untuk saling memperkuat.', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(8, 'STUDIUM GENERALE PASCASARJANA UAD 2019', 'vtGz1eyV8hs', '00:01:57', 'Program Studi Pascasarjana Universitas Ahmad Dahlan mengadakan Studium Generale Mahasiswa baru Pascasarjana semester genap 2018/2019 dengan berjumlah 222 Mahasiswa baru dari 12 Program Studi yang diadakan di Auditorium Kampus 3 UAD, sabtu (09/03/2019). Studium Generale dengan tema “Kebijakan Pemerintah dalam Bidang Ekonomi untuk Mendukung Pencapaian SDGs (Sustainable Development Goals)” dengan pembicara Dr. Taufik Hidayat, M.Ec, Direktur Komite Nasional Keuangan Syariah (KNKS).', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, '2019-04-01 01:01:01', NULL, NULL, 'kneks', NULL),
(9, 'BAZNAS News - Sinergi KNKS dengan BAZNAS menuju Indonesia sebagai Pusat Ekonomi Syariah Dunia', 'bv3uErg_dNs', '00:02:11', '<p>Pertemuan antara KNKS dan BAZNAS pada 31 Januari, membahas bagaimana pentingnya peran zakat yang signifikan dalam membangun visi Indonesia menjadi pusat ekonomi syariah dunia. Kerjasama yang dilakukan antara lain penelitian, pengembangan regulasi dan pengembangan sistem informasi. Apa yang akan dikerjakan oleh KNKS justru telah banyak dilakukan BAZNAS sehingga kerjasama ini berkomitmen untuk saling memperkuat.</p>', '2019-06-14 03:50:46', '2019-06-14 03:50:46', NULL, '2019-06-14 10:50:41', NULL, NULL, 'kneks', NULL),
(16, 'ss', 'https://www.youtube.com/watch?v=KI3TyA7RQA0', '00:00:00', 'dd', '2024-12-17 16:39:24', '2024-12-17 16:39:24', NULL, '2024-12-17 23:39:00', 'dd', '<br>', 'kneks', '[{"value":"indonesia"},{"value":"kneks"},{"value":"kdeks"},{"value":"syariah"}]');

