(205, 'Modul 6 Melaksanakan Prinsip-Prinsip Syariah dalam Pembiayaan dan Pendanaan', '2024-05-22', 'https://webdev.rifhandi.com/uploads/filesupload/sample.pdf', '<p>Modul 6 Pelatihan Berbasis Kompetensi (PBK) - Melaksanakan Prinsip-Prinsip Syariah dalam Pembiayaan dan Pendanaan</p>', 1, '2024-05-27 09:24:33', '2024-05-27 09:24:33', NULL, 7, NULL, NULL, 'kneks'),
(206, 'Modul 7 Melaksanakan Prinsip-Prinsip Syariah dalam Pembiayaan dan Pendanaan', '2024-05-22', 'https://webdev.rifhandi.com/uploads/filesupload/sample.pdf', '<p>Modul 7 Pelatihan Berbasis Kompetensi (PBK) - Melaksanakan Prinsip-Prinsip Syariah dalam Pembiayaan dan Pendanaan</p>', 1, '2024-05-27 09:25:03', '2024-05-27 09:25:03', NULL, 7, NULL, NULL, 'kneks'),
(207, 'Modul 8 Melaksanakan Prinsip-Prinsip Syariah dalam Pembiayaan dan Pendanaan', '2024-05-22', 'https://webdev.rifhandi.com/uploads/filesupload/sample.pdf', '<p>Modul 8 Pelatihan Berbasis Kompetensi (PBK) - Melaksanakan Prinsip-Prinsip Syariah dalam Pembiayaan dan Pendanaan</p>', 1, '2024-05-27 09:25:40', '2024-05-27 09:25:40', NULL, 7, NULL, NULL, 'kneks'),
(208, 'Masterplan Industri Halal Indonesia 2023-2029', '2024-06-04', 'https://webdev.rifhandi.com/uploads/filesupload/sample.pdf', '<p>MPIHI 2023-2029 merupakan road map pengembangan industri halal dari tahun 2023 sampai dengan tahun 2029, dengan 4 strategi utama, yaitu 1) peningkatan produktivitas dan daya saing; 2) penerapan serta penguatan kebijakan dan regulasi; 3) penguatan keuangan dan infrastruktur; serta 4) penguatan halal brand and awareness, yang didukung juga dengan 11 program utama, dan 8 indikator yang akan dipantau perkembangannya secara berkala.</p>', 1, '2024-06-04 10:40:59', '2024-06-23 08:04:18', NULL, 1, NULL, NULL, 'kneks'),
