INSERT INTO `scopes` (`id`, `title`, `icon`, `image`, `description`, `created_at`, `updated_at`, `deleted_at`, `title_en`, `description_en`, `web_identity`) VALUES
(2, 'Penguatan Halal Value Chain Indonesia', '', '', '<p id="val3">Memperkuat seluruh rantai nilai industri halal dari hulu ke hilir melalui pembangunan halal hub di daerah, pengembangan standar halal, kampanye gaya hidup halal, penyediaan insentif bagi pelaku usaha dan pembangunan pusat halal internasional.</p>', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, 'Strengthening Halal Value Chain', '<p id="val4">Strengthening the entire value chain of the halal industry from upstream to downstream through the development of halal hubs in the regions, developing halal standards, halal lifestyle campaigns, providing incentives for businesses and building international halal centers.</p>', 'kneks'),
(3, 'Penguatan Usaha Mikro, Kecil, dan Menengah (UMKM)', '../../images/lingkup/icons/ic-ekonomi-dan-keuangan-mikro-syariah.svg', '../../images/lingkup/img-ekonomi-dan-keuangan-mikro-syariah.jpg', 'Memperkuat pelaku UMKM industri halal dan mendorong pencapaian pemerataan kesejahteraan dan kemandirian ekonomi bangsa melalui penyediaan program edukasi dan literasi, penyediaan fasilitas pembiayaan syariah terintegrasi, serta pembangunan database UMKM.', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, 'Strengthening Micro, Small and Medium Enterprises (MSMEs)', 'Strengthen the halal MSME industry and encourage the achievement of equitable welfare and independence of the nation''s economy through the provision of education and literacy programs, the provision of integrated Islamic financing facilities, and the construction of MSME databases.', 'kneks'),
(4, 'Penguatan Fatwa, Regulasi dan Tata Kelola', '../../images/lingkup/icons/ic-dana-sosial-keagamaan.svg', '../../images/lingkup/img-dana-sosial-keagamaan.jpg', 'Memperkuat penyediaan iklim usaha Industri Keuangan Syariah Dan Industri Halal dengan adanya kepastian hukum, proses yang mudah dan tata kelola yang baik.', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, 'Strengthening Fatwa, Regulations and Governance', 'Strengthening the provision of a business climate for the Sharia Financial Industry and Halal Industry with legal certainty, easy processes and good governance.', 'kneks'),
(5, 'Penguatan Ekonomi Digital', '../../images/lingkup/icons/ic-ekonomi-digital.svg', '../../images/lingkup/img-ekonomi-digital.jpg', 'Memperkuat pelaku industri halal dengan memanfaat dan mengoptimalkan layanan digital baik pembiayaan, pemasaran dan kapasitas produksi melalui penyediaan hala market place, pembentukan incubator start-up, dan sistem informasi terintegrasi untuk traceability produk halal.', '2019-05-09 08:05:42', '2019-05-09 08:05:42', NULL, 'Strengthening the Digital Economy', 'Strengthening halal industry players by utilizing and optimizing digital services both financing, marketing and production capacity through the provision of hala market place, establishment of incubator start-ups, and integrated information systems for traceability of halal products.', 'kneks');

