INSERT INTO `abouts` (`id`, `title`, `title_en`, `tag`, `content`, `content_en`, `created_at`, `web_identity`, `updated_at`, `deleted_at`) VALUES
(1, 'Tentang KNEKS', 'About KNEKS', 'about', '<p>Komite Nasional Ekonomi dan Keuangan Syariah (KNEKS) merupakan perubahan dari KNKS untuk peningkatan pembangunan ekosistem ekonomi dan keuangan syariah serta menjadikan Indonesia sebagai Pusat Halal Dunia.</p>\r\n\r\n<p>Pencanangan titik awal untuk memposisikan Indonesia sebagai salah satu pelaku utama dan hub ekonomi syariah dunia dilakukan seiring dengan peluncuran Masterplan Ekonomi Syariah Indonesia pada bulan Mei 2019.</p>', NULL, '2019-05-09 08:05:42', 'kneks', '2020-04-02 07:49:30', NULL),
(2, 'Sejarah KNEKS', 'KNEKS History', 'history', '<p id="val3"><p id="val3"></p><p>Dalam rangka mendukung pembangunan ekonomi nasional dan mendorong percepatan pengembangan sektor keuangan syariah, pemerintah secara khusus mendirikan KNKS pada tanggal 8 November 2016 agar dapat meningkatkan efektifitas, efisiensi pelaksanaan rencana pembangunan nasional bidang keuangan dan ekonomi Syariah. Selanjutnya sejak diundangkan tanggal 10 Februari 2020, pemerintah melakukan perubahan Komite Nasional Keuangan Syariah menjadi Komite Nasional Ekonomi dan Keuangan Syariah yang bertujuan meningkatkan pembangunan ekosistem ekonomi dan keuangan syariah guna mendukung pembangunan ekonomi nasional.</p><p></p></p>', '<p id="val4"><p id="val4"></p></p>', '2019-05-09 08:05:42', 'kneks', '2020-05-21 02:10:07', NULL),
(5, 'Fungsi', 'FUNCTION', 'function', '<p id="val3"><ul>\r\n	<li>Pemberian rekomendasi arah kebijakan dan program strategis pembangunan nasional di sektor ekonomi dan keuangan syariah.</li>\r\n	<li>Pelaksanaan koordinasi, sinkronisasi, sinergisitas penyusunan dan pelaksanaan rencana araha kebijakan dan program strategis pada sektor ekonomi dan keuangan syariah.</li>\r\n	<li>Perumusan dan pemberian rekomendasi atas penyelesaian masalah di sektor ekonomi dan keuangan syariah.</li>\r\n	<li>Pemantauan dan evaluasi atas pelaksanaan arah kebijakan dan program strategis di sektor ekonomi dan keuangan syariah.</li>\r\n</ul></p>', '<p id="val4"></p>', '2019-05-09 08:05:42', 'kneks', '2020-04-02 07:49:53', NULL);
